import PropTypes from 'prop-types';

export default function textform(prop) {
  const[text,setText]=useState("enter the the text here")
  const convert=()=>{
    console.log("convert text");
  }
  return (
    <div className="mb-3 text-center h3">
    <label for="exampleFormControlTextarea1" class="form-label">{prop.line}</label>
    <textarea class="form-control container-sm  border-secondary" id="exampleFormControlTextarea1" rows="3" value={text} ></textarea>
    <div  id="btnclass"className='text-start m'><button className=' btn btn-primary my-2'  onClick={convert}>Convert to uppercase</button></div>
  </div>
  )
}
textform.propTypes={
    line:PropTypes.string.isRequired
}
