import React,{useState} from 'react'
import PropTypes from 'prop-types';

export default function Textform(prop) {
  document.getElementById('replace_').addEventListener('click',()=>{
    
  })
   
  const uppercase=()=>{
    console.log("convert text");
    let newtext=count.toUpperCase();
    setCount(newtext)
  }
  const lowercase=()=>{
    let lowertext=count.toLowerCase();
    setCount(lowertext)
  }
  const entertext=(event)=>
{
      setCount(event.target.value)
}
  const [count,setCount]=useState("");
  return (
   <>
    <div className="mb-3 text-center h3">
    <label htmlFor="exampleFormControlTextarea1" className="form-label">{prop.line}</label>
    <textarea className="form-control container-sm  border-secondary" id="exampleFormControlTextarea1" rows="3" value={count} onChange={entertext} ></textarea>
    <div  id="btnclass"className='text-start m'>
      <button className=' btn btn-primary my-2 mx-2'  onClick={uppercase}>Convert to uppercase</button>
      <button className='btn btn-dark mx-2 my-2' onClick={lowercase}>convert to uppercase</button>
      <button className='btn btn-secondary mx-2 my-2 ' id='replace_' >Replace </button>
      </div>
  </div>
  <div  id='display' className="container">
  

  </div>
     <h2 className='text-center textsummary'>Text Summary</h2>
     <div className="container">
       <p id='info' className='h5 text-primary'>The number of  character is{count.split(" ").length}</p>
       <p id='info' className='h5 text-primary'>The number of words is {count.length}</p>
       <p  id='info' className='h5 text-primary'>The to read the text {(1/238)*count.split(" ").length} minutes </p>
     </div>
      <h2 id='h' className='textsummary text-center'>Preview</h2>
      <p className=' container'>
        {
          count
        }
      </p>
   </>
  )
}
Textform.propTypes={
    line:PropTypes.string.isRequired
}
