import React from 'react'
import PropTypes from 'prop-types';

export default function textform(prop) {
  return (
    <div className="mb-3 text-center h3">
    <label for="exampleFormControlTextarea1" class="form-label">{prop.line}</label>
    <textarea class="form-control container-sm  border-secondary" id="exampleFormControlTextarea1" rows="3" ></textarea>
    <button className='btn -primary'>Convert to uppercase</button>
  </div>
  )
}
textform.propTypes={
    line:PropTypes.string.isRequired
}
