import Navbar from './components/Navbar'
import Textform from './components/textform'
import './App.css';
import React,{useState}from 'react'
import Alert from './components/alert'
function App() {
  const [mode, setMode] = useState('Dark Mode');
  const [mystyle,setMystyle]=useState(
    'black'
  )
  const [alert, setAlert] = useState(null)
  const doalert=(message,type)=>{
    setAlert({
         msg:message,
         types:type
    })
 }
  const [mystyle2,setMystyle2]=useState("white")
  const onhandlechange = () => {
    console.log('hello')
    if (mode==='Dark Mode') {
      setMode('Light Mode')
      setMystyle('#00000087')
      setMystyle2('black')
    }
    else
    {
      setMode('Dark Mode')
      setMystyle('black')
      setMystyle2('white')
    }
  }
  return (
    <>
      {<Navbar method="Strings" title="Conversion" Array="Array Function" modes={mode} mystyle={mystyle} mystyle2={mystyle2} togglechange={onhandlechange} />}
      <Alert alert/>
      <Textform line="Enter the text for conversion" mystyle={mystyle} mystyle2={mystyle2}/>
    </>
  );
}

export default App;
